import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
/**
 * Another brute force algorithm
 * but instead of checking pointerB - pointerA
 * this algorithm checks the count of appearance of each legal word.
 * Since it only uses the two pointer trick, the running time is still O(mn)
 * @author hpPlayer
 * @date Jul 29, 2015 8:36:48 PM
 */

public class p030_sol3 {
	public static void main(String[] args) {
		String s = "barfoofoobarthefoobarman";
		String[] words = { "bar", "foo", "the" };
		//String s = "wordgoodgoodgoodbestword";
		//String[] words = {"word","good","best","good"};
		//String s = "lingmindraboofooowingdingbarrwingmonkeypoundcake";
		//String[] words = {"fooo","barr","wing","ding","wing"};
		
		
		//String s1 = "";
		//String s2 = "";
		//String s3 = "";
		//String[] words = {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"};
		
		System.out.println(new p030_sol3().findSubstring(s, words));
	}
    public List<Integer> findSubstring(String s, String[] words) {
        List<Integer> res = new ArrayList<Integer>();
        int num = words.length, len = words[0].length();
        HashMap<String, Integer> hs = new HashMap<String, Integer>();
        for (String temp : words){
        	if(!hs.containsKey(temp)){
        		hs.put(temp, 1);
        	}else{
        		hs.put(temp, hs.get(temp) + 1);
        	}
        }
        
        int i = 0;//record start index
        while ( (i + num * len - 1) < s.length()){
        	HashMap<String, Integer> hs2 = new HashMap<String, Integer>();
        	int j = 0;//count legal words used
        	while (j < num){
        		String subs = s.substring(i + j * len, i + (j + 1) * len);
        		if(!hs.containsKey(subs)){//words does not contain such string
        			break;
        		}else{
        			if(!hs2.containsKey(subs)){
        				hs2.put(subs, 1);
        			}else{
        				hs2.put(subs, hs2.get(subs) + 1);
        			}
          			if(hs2.get(subs) > hs.get(subs)){//we got extra word in words
        				break;
        			}
        			j++;//legal word, count ++
        		}
        	}
        	if(j == num){
        		res.add(i);
        	}
        	i++;
        }
        return res;
    }
}
